onHit.script:
// Kass - Dagger Hit Phrases
                                // Phrases for when Val hits with her dagger

                                const daggerHitPhrases = [
                                  "SKREE! Small-talon finds prey-weakness! Thief-skill!",
                                  "Quick-strike like hawk-claw! Shadow-dancer perfect!",
                                  "Small-blade big-pain! Prey-squeal pleases sky-sister!",
                                  "Hidden-fang bites deep! Sneak-hunter success!",
                                  "Short-steel finds blood-path! Elf-precision!",
                                  "Thief-talons strike true! Val-sister skilled!",
                                  "KEE! Little-bite big-hurt! Magic-thief deadly!",
                                  "Celene-trained knife-work makes wing-sister proud!",
                                  "Dagger-flash faster than prey-blink! Good!",
                                  "Silent-steel speaks blood-language! Hunt-joy!",
                                  "Treasure-taker's blade finds vital-spot! Perfect!",
                                  "Quick-talon between shell-gaps! Smart hunter!",
                                  "Night-blade drinks deep! Enstad-training shows!",
                                  "Spell-weaver's small-fang finds prey-life!",
                                  "Secret-striker earns sky-sister's approval! Blood-win!"
                                ];

                                // Select a random phrase
                                const randomPhrase = daggerHitPhrases[Math.floor(Math.random() * daggerHitPhrases.length)];

                                // Create the chat message
                                let chatData = {
                                  user: game.user._id,
                                  speaker: ChatMessage.getSpeaker(),
                                  content: `<div style="background-color: #213631; border: 1px solid #4e7561; border-radius: 5px; padding: 10px; color: #c9e6ff; text-shadow: 0 0 5px #7abed6;">
                                    <span style="font-weight: bold; font-style: italic; color: #7abed6;">Kass telepathically communicates:</span><br>
                                    <span style="font-family: 'Arial', sans-serif; font-size: 14px;">"${randomPhrase}"</span>
                                  </div>`
                                };

                                // Send the message to chat
                                ChatMessage.create(chatData, {});

